// Filter and prepare messages for deletion
const messages = $input.all();
const config = $('Set Configuration').first().json;

const messagesToDelete = [];
const currentYear = new Date().getFullYear();
const targetYear = parseInt(config.deleteBeforeYear);

messages.forEach((item) => {
  const message = item.json;

  // Extract date from message
  let messageDate = null;
  if (message.internalDate) {
    messageDate = new Date(parseInt(message.internalDate));
  } else if (message.payload && message.payload.headers) {
    const dateHeader = message.payload.headers.find(h => h.name.toLowerCase() === 'date');
    if (dateHeader) {
      messageDate = new Date(dateHeader.value);
    }
  }

  if (messageDate && messageDate.getFullYear() < targetYear) {
    messagesToDelete.push({
      id: message.id,
      threadId: message.threadId,
      subject: message.payload?.headers?.find(h => h.name === 'Subject')?.value || 'No Subject',
      from: message.payload?.headers?.find(h => h.name === 'From')?.value || 'Unknown',
      date: messageDate.toISOString().split('T')[0],
      year: messageDate.getFullYear()
    });
  }
});

// Generate summary
const summary = {
  totalFound: messages.length,
  toDelete: messagesToDelete.length,
  oldestYear: messagesToDelete.length > 0 
    ? Math.min(...messagesToDelete.map(m => m.year))
    : null,
  newestYear: messagesToDelete.length > 0
    ? Math.max(...messagesToDelete.map(m => m.year))
    : null,
  messages: messagesToDelete.slice(0, 50) // Limit preview to 50
};

return messagesToDelete.map(msg => ({
  json: {
    messageId: msg.id,
    threadId: msg.threadId,
    subject: msg.subject,
    from: msg.from,
    date: msg.date,
    year: msg.year,
    summary: summary
  }
}));

