// Generate simple Markdown format
const items = $input.all();

let markdown = '# ðŸ“ Google Drive Folders\n\n';
markdown += `**Total:** ${items.length} folders\n\n`;
markdown += '| # | Folder Name | Folder ID | Modified Date |\n';
markdown += '|---|-------------|-----------|---------------|\n';

items.forEach((item, index) => {
  const folder = item.json;
  const name = folder.name || 'Unnamed';
  const id = folder.id || 'N/A';
  const modified = folder.modifiedTime ? new Date(folder.modifiedTime).toLocaleDateString() : 'N/A';
  markdown += `| ${index + 1} | ${name} | \`${id}\` | ${modified} |\n`;
});

return [{
  json: {
    markdown: markdown,
    totalFolders: items.length,
    folders: items.map(item => ({
      name: item.json.name,
      id: item.json.id,
      link: item.json.webViewLink || `https://drive.google.com/drive/folders/${item.json.id}`
    }))
  }
}];
