// Format messages for readable output
const items = $input.all();
let messages = [];

// Extract messages from API response
if (items[0] && items[0].json.data) {
  messages = items[0].json.data.slice(0, 10);
}

// Format each message
const formattedMessages = messages.map((msg, index) => {
  return {
    number: index + 1,
    from: msg.from || 'Unknown',
    to: msg.to || 'Unknown',
    message: msg.text?.body || msg.type || 'No text',
    timestamp: msg.timestamp || 'Unknown',
    type: msg.type || 'text',
    messageId: msg.id || 'N/A'
  };
});

// Generate readable output
let readableOutput = '# ðŸ“± Last 10 WhatsApp Messages\n\n';
readableOutput += `**Total Messages:** ${formattedMessages.length}\n\n`;
readableOutput += '---\n\n';

formattedMessages.forEach((msg, index) => {
  readableOutput += `## ${index + 1}. Message from ${msg.from}\n\n`;
  readableOutput += `- **From:** ${msg.from}\n`;
  readableOutput += `- **To:** ${msg.to}\n`;
  readableOutput += `- **Message:** ${msg.message}\n`;
  readableOutput += `- **Type:** ${msg.type}\n`;
  readableOutput += `- **Timestamp:** ${new Date(parseInt(msg.timestamp) * 1000).toLocaleString()}\n`;
  readableOutput += `- **Message ID:** ${msg.messageId}\n`;
  readableOutput += '\n---\n\n';
});

// Generate markdown table
let markdownTable = '| # | From | Message | Type | Timestamp |\n';
markdownTable += '|---|------|---------|------|-----------|\n';

formattedMessages.forEach((msg, index) => {
  const date = new Date(parseInt(msg.timestamp) * 1000).toLocaleString();
  const shortMsg = msg.message.length > 50 ? msg.message.substring(0, 50) + '...' : msg.message;
  markdownTable += `| ${index + 1} | ${msg.from} | ${shortMsg} | ${msg.type} | ${date} |\n`;
});

return [{
  json: {
    totalMessages: formattedMessages.length,
    messages: formattedMessages,
    readableOutput: readableOutput,
    markdownTable: markdownTable,
    rawData: items[0]?.json || {}
  }
}];
