// Format the data nicely
const posts = $input.all();

let markdownOutput = '# ðŸ“Š Big Data Output\n\n';
markdownOutput += `**Total Posts:** ${posts.length}\n`;
markdownOutput += `**Generated:** ${new Date().toISOString()}\n\n`;
markdownOutput += '---\n\n';

posts.forEach((item, index) => {
  const post = item.json;
  markdownOutput += `## ${index + 1}. ${post.title}\n\n`;
  markdownOutput += `**Post ID:** ${post.id}\n`;
  markdownOutput += `**User ID:** ${post.userId}\n\n`;
  markdownOutput += `**Content:**\n${post.body}\n\n`;
  markdownOutput += '---\n\n';
});

let tableOutput = '| # | ID | User | Title |\n';
tableOutput += '|---|----|----|-------|\n';

posts.forEach((post, index) => {
  const shortTitle = post.title.length > 50 ? post.title.substring(0, 50) + '...' : post.title;
  tableOutput += `| ${index + 1} | ${post.id} | ${post.userId} | ${shortTitle} |\n`;
});

return [{
  json: {
    totalPosts: posts.length,
    posts: posts,
    markdownOutput: markdownOutput,
    tableOutput: tableOutput,
    summary: `âœ… Retrieved ${posts.length} posts successfully!`
  }
}];
